package test

import (
	"encoding/hex"
	"fmt"
	"github.com/EmYiQing/Gososerial/ysoserial"
	"strings"
	"testing"
)

func TestCC6(t *testing.T) {
	ser := ysoserial.GetCC6("calc.exe")
	serStr := strings.ToUpper(hex.EncodeToString(ser))
	stdStr := ""
	fmt.Println(serStr)
	fmt.Println(stdStr)
	fmt.Println(serStr == stdStr)
}
